mapper.select:
SELECT COUNT(*)
FROM recommend
WHERE recNo=${param1}

select recNo, recTitle, recHashtag, recDescription, recLevel, recommendSendData, date_format(recWritedate,'%m-%d %H:%i') recWritedate from recommend
where ${searchKey} like '%${searchWord}%'
order by recNo desc limit ${onePageRecord} offset ${offsetIndex}
